    private void On721TrxCompleted(string response)
    {
        TransactionHash.text = "Transaction Hash:-" +response;
        ToastManager.Instance.Show("ERC-721 NFT Minted Successfully!!", ToastType.Success);
        Debug.Log("On721TrxCompleted");
        send721Trx.interactable = true;
    }
    private void On721TrxFailed(string res)
    {
        ToastManager.Instance.Show("Transaction Failed!!", ToastType.Error);
        Debug.Log("TRX FAILED for ERC-721 " + res);
        send721Trx.interactable = true;
    }

    private void On1155TrxCompleted(string response)
